import { Directive, OnInit, Input, ElementRef, HostListener, Renderer } from '@angular/core';
import * as Clipboard from 'clipboard';
import { File } from '../services/models/file';

@Directive({
  selector: '[drClipboard]',
})
export class ClipboardDirective  {

  @Input() file: File;
  private _button: HTMLElement;

  @HostListener('mouseenter') onMouseEnter() {
    if (!this._button) {
      this._button = this._renderer.createElement(this._element.nativeElement, 'button');
      this._button.innerHTML = 'copy';
      this._button.className = 'copybutton';
      this._button.setAttribute('md-raised-button', null);
      this._renderer.listen(this._button, 'click', () => { this._copyToClipboard(); });
    } else {
      this._button.className = 'copybutton';
    }
  }

  @HostListener('mouseleave') onMouseLeave() {
    if (this._button) {
      this._button.className = 'hiddenbutton';
    }
  }

  constructor(private _element: ElementRef, private _renderer: Renderer) { }

  private _copyToClipboard() {
    const clipboard = new Clipboard(this._element.nativeElement, { text: () => this.file.content });
    clipboard.on('success', (e) => { clipboard.destroy(); });
  }
}

