import { AuthService } from '../../services/auth.service';
import { Blog } from '../services/models/blog';
import { PreventDeleteDialogComponent } from '../../shared/prevent-delete-dialog.component';

@Component({
  selector: 'dr-item',
  templateUrl: './item.component.html',
  styleUrls: ['./item.component.scss']
})
export class ItemComponent implements OnInit, OnDestroy {

  public blog: Blog;
  public editMode: boolean;
  private _blogSubscription: Subscription;

  constructor(public route: ActivatedRoute, public router: Router, public blogService: BlogService, public authService: AuthService,
      public dialog: MdDialog, public snackBar: MdSnackBar) {
    this.editMode = false;
  }

  ngOnInit() {
    this.route.params.forEach((params: Params) => {
      const key = params['key'];
      if (key === 'new') {
        this.blog = null;
        this.editMode = true;
      } else {
        this._blogSubscription = this.blogService.getBlog(key).subscribe(data => this.blog = data);
      }
    });
  }

  ngOnDestroy() {
    if (this._blogSubscription) { this._blogSubscription.unsubscribe(); }
  }

  onEditClick() {
    if (this.authService.user.isAdmin) {
      this.editMode = true;
    }
  }

  onSave() {
    if (this.authService.user.isAdmin) {
      if (this.blog) {
        console.log('saving', this.blog.key);
      } else {
        console.log('creating new blog');
        this.router.navigate(['/']);
      }
      this.editMode = false;
    }
  }

  onDelete() {
    if (this.authService.user.isAdmin){
      const dialogRef = this.dialog.open(PreventDeleteDialogComponent);
      dialogRef.afterClosed().subscribe(result => {
        if (result) {
          const snackBarRef = this.snackBar.open('deleting', null, { duration: 3000 });
          console.log('deleting', this.blog.key);
        }
      });
    }
  }

}

import { Component, ElementRef, Input, Output, EventEmitter } from '@angular/core';
import { FormGroup, FormArray } from '@angular/forms';
import { MdDialog, MdSnackBar } from '@angular/material';
import { File } from '../services/models/file';
import { PreventDeleteDialogComponent } from '../../shared/prevent-delete-dialog.component';

@Component({
  selector: 'dr-item-edit-section',
  templateUrl: './item-edit-section.component.html',
  styleUrls: ['./item-edit-section.component.scss']
})
export class ItemEditSectionComponent {

  @Input() form: FormGroup;
  @Input() showSwap: boolean;
  @Input() hideDelete: boolean;
  @Output() moveSwap = new EventEmitter();
  @Output() deleteSection = new EventEmitter();
  @Output() addSection = new EventEmitter();
  @Output() addFile = new EventEmitter();

  public editText: boolean;

  constructor(private _elementRef: ElementRef, public dialog: MdDialog, public snackBar: MdSnackBar) {
    this.editText = false;
  }

  onSwapSection() {
    this.moveSwap.emit();
  }

  onDeleteSection() {
    const dialogRef = this.dialog.open(PreventDeleteDialogComponent);
    dialogRef.afterClosed().subscribe(result => {
      if (result) {
        this.deleteSection.emit();
      }
    });
  }

  onAddSection() {
    this.addSection.emit();
  }

  onTextClick(e: Event, el: HTMLElement) {
    this.editText = true;
    setTimeout((x) => {
      this._elementRef.nativeElement.querySelector('textarea').focus();
    }, 100);
  }

  onTextAreaFocusOut() {
    this.editText = false;
  }

  onAddFile() {
    this.addFile.emit();
  }

  onSwapFile(order: any) {
    const control = <FormArray>this.form.controls['files'];
    const values = <File[]>control.getRawValue();
    const obj1 = values.find((x) => x.order === order);
    const obj2 = values.find((x) => x.order === order + 1);
    if (obj1 && obj2) {
      obj1.order ++;
      obj2.order --;
    }
  }

  onDeleteFile(order: any) {
    const dialogRef = this.dialog.open(PreventDeleteDialogComponent);
    dialogRef.afterClosed().subscribe(result => {
      if (result) {
        this._deleteFile(order);
      }
    });
  }

  private _deleteFile(order: any) {
    const control = <FormArray>this.form.controls['files'];
    const index = control.getRawValue().findIndex((x) => x.order === order);
    control.removeAt(index);
    const values = <File[]>control.getRawValue();
    values.forEach((x) => { if (x.order >= order) { x.order--; }});
    control.patchValue(values);
  }

}

import { AngularFire,  AngularFireModule } from 'angularfire2';
import { ReflectiveInjector, Injector } from '@angular/core';
import { BlogService } from '../services/blog.service';
import { Observable } from 'rxjs/Observable';

export class NoDuplicationKeyValidator {

  static checkNoDuplication(_blogService: BlogService, currentKey: string): {[key: string]: any} {
    return (control) => {
      return new Observable((obs: any) => {
        if (!control.valueChanges || control.value === currentKey) {
          obs.next(null);
          obs.complete();
        } else {
          control
            .valueChanges
            .debounceTime(300)
            .flatMap(value => _blogService.getBlog(control.value))
            .subscribe(
              data => {
                if (data) {
                  obs.next({ ['duplicated']: true });
                } else {
                  obs.next(null);
                }
                obs.complete();
              },
              error => {
                obs.next({ ['duplicated']: true });
                obs.complete();
              }
          );
        }
      });
    };
  }
}

import { Component, OnChanges, Input, Output, EventEmitter } from '@angular/core';
import { FormBuilder, FormGroup, Validators, FormArray } from '@angular/forms';
import * as moment from 'moment';
import { Blog } from './../services/models/blog';
import { Section } from './../services/models/section';
import { File } from './../services/models/file';
import { BlogService } from '../services/blog.service';

import { NoDuplicationKeyValidator } from '../validators/noduplication-key.validator';

@Component({
  selector: 'dr-item-edit',
  templateUrl: './item-edit.component.html',
  styleUrls: ['./item-edit.component.scss']
})
export class ItemEditComponent implements OnChanges {

  @Input() blog: Blog;
  @Output() save = new EventEmitter<Blog>();

  public form: FormGroup;

  constructor(private formBuilder: FormBuilder, private blogService: BlogService) { }

  ngOnChanges() {
    this.form = null;
    this._fillForm();
  }

  onSave(ev: MouseEvent) {
    ev.preventDefault();
    this.save.emit(this.form.value);
  }

  onDeleteSection(order: number) {
    const control = <FormArray>this.form.controls['sections'];
    const index = control.getRawValue().findIndex((x) => x.order === order);
    control.removeAt(index);
    const values = <Section[]>control.getRawValue();
    values.forEach((x) => { if (x.order >= order) { x.order--; }});
    control.patchValue(values);
  }

  onSwapSection(order: any) {
    const control = <FormArray>this.form.controls['sections'];
    const values = <Section[]>control.getRawValue();
    const obj1 = values.find((x) => x.order === order);
    const obj2 = values.find((x) => x.order === order + 1);
    if (obj1 && obj2) {
      obj1.order ++;
      obj2.order --;
    }
  }

  onAddSection(order: any) {
    const control = <FormArray>this.form.controls['sections'];
    const values = <Section[]>control.getRawValue();
    values.forEach((x) => { if (x.order > order) { x.order++; }});
    control.patchValue(values);
    control.push(this._fillSection({ order: order  + 1, text: null, files: null }));
  }

  onAddFile(order: any) {
    const control = <FormArray>this.form.controls['sections'];
    const index = control.getRawValue().findIndex((x) => x.order === order);
    const section = <FormArray>(<FormGroup>control.at(index)).controls['files'];
    const values = <File[]>section.getRawValue();
    section.push(this._fillFile({ order: values.length + 1 , content: null, name: null, path: null }));
  }

  private _fillForm() {
    const blog = this.blog || { key: '', header: '', title: '', date_published: '', sections: [null] };
    this.form = this.formBuilder.group({
      key: [blog.key,  [Validators.required], [NoDuplicationKeyValidator.checkNoDuplication(this.blogService, blog.key) ] ],
      header: blog.header,
      title: [blog.title, [  Validators.required ]],
      date_published: blog.date_published ? moment(blog.date_published).format('YYYY-MM-DDTMM:ss') : null,
      sections: this.formBuilder.array(this._fillSectionArray(blog.sections))
    });
  }

  private _fillSectionArray(sections: Section[]): any[] {
    const result: any[] = [];
    (sections || [null]).forEach(element => { result.push(this._fillSection(element)); });
    return result;
  }

  private _fillSection(data: Section) {
    const order: number = data ? data.order : this.form && this.form.controls['sections'] ?
      (<Section[]>(<FormArray>this.form.controls['sections']).getRawValue()).length + 1 : 1;

    const section = {
        order: order,
        text: data ? data.text : '',
        files: this.formBuilder.array(data && data.files && data.files.length > 0 ? this._fillFileArray(data.files) : [])
      };
    return this.formBuilder.group(section);
  }

  private _fillFileArray(files: File[]) {
    const result: any[] = [];
    (files || [null]).forEach(element => { result.push(this._fillFile(element)); });
    return result;
  }

  private _fillFile(data: File) {
    const order: number = data ? data.order : this.form && this.form.controls['files'] ?
      (<File[]>(<FormArray>this.form.controls['files']).getRawValue()).length + 1 : 1;

    const file: any = {
      order: order,
      name: data ? data.name : '',
      content: data ? data.content : '',
      path: data ? data.path : ''
    };
    return this.formBuilder.group(file);
  }

}

import { Injectable, OnInit } from '@angular/core';
import { Observable, Subject } from 'rxjs/Rx';
import { AngularFire } from 'angularfire2';
import { Query } from 'angularfire2/interfaces';
import * as moment from 'moment';
import * as _ from 'lodash';
import 'rxjs/Rx';
import { AuthService } from '../../services/auth.service';
import { Blog } from './models/blog';

@Injectable()
export class BlogFirebaseService {

  public get page(): number {
    return this._page;
  }

  public get pages(): number[] {
    return Array(Math.ceil(this._count / this._blogsByPage)).fill(null).map((x, i) => i + 1);
  }

  private _count: number;
  private _page: number;
  private _blogsByPage: number;

  private _localCache: Blog[] = [];
  private _blogSubject: Subject<Blog[]>;

  constructor(public af: AngularFire, public authService: AuthService) {
    this._page = 1;
    this._count = 0;
    this._blogsByPage = 3;

    this._blogSubject = new Subject<Blog[]>();
    this.af.database.list('/blog')
      .map((x: Blog[]) => { return x; })
      .do((x) => { this._localCache = x; })
      .do((x) => this._blogSubject.next(x))
      .subscribe();
  }

  // method to get all blogs
  getBlogs(): Observable<Blog[]> {
    const localObservable = Observable.of(this._localCache);
    return Observable.merge(localObservable, this._blogSubject.asObservable())
      .map((blogs) => {
        const partial = this.authService.user.isAdmin ? _.orderBy(blogs, (x) => x.date_created)
          : _.orderBy(_.filter(blogs, (x) => x.date_published && moment(x.date_published).isSameOrBefore(moment.utc())),
             (x) => x.date_published);
        this._count = partial.length;
        return partial.reverse().slice((this.page - 1) * this._blogsByPage, (this.page) * this._blogsByPage);
      });
  }

  // method to get on blog by key
  getBlog(key: string): Observable<Blog> {
    const blog = this._localCache.filter(x => x.key === key);
    const localObservable = Observable.of(blog);
    return Observable.merge(localObservable, this._blogSubject.asObservable())
      .map(res => { return res.find(x => x.key === key); });
  }

  refresh() {
    this._blogSubject.next(this._localCache);
  }

  setPage(num: number) {
    this._page = num;
    this._blogSubject.next(this._localCache);
  }

  saveBlog(id: string, blog: Blog): Observable<string> {
    const subject = new Subject<string>();
    blog.date_published = blog.date_published ? moment(blog.date_published).toDate() : null;
    if (id) {
      this.af.database.list('/blog').update(id, blog)
        .then(() => { subject.next(null); })
        .catch((e: Error) => { subject.next(e.message); });
    } else {
      blog.date_created = moment.utc().toDate();
      this.af.database.list('/blog').push(blog)
        .then((x) => { subject.next(null); })
        .catch((e: Error) => { subject.error(e.message); } );
    }
     return subject.asObservable();
  }

  deleteBlog(id: string): Observable<string> {
    const subject = new Subject<string>();
    this.af.database.list('/blog').remove(id)
      .then(() => { subject.next(null); })
      .catch((e: Error) => { subject.error(e.message); } );
    return subject.asObservable();
  }

}

import { Injectable } from '@angular/core';
import { Http } from '@angular/http';
import { Observable, Subject } from 'rxjs/Rx';
import { Blog } from './models/blog';
import 'rxjs/Rx';

@Injectable()
export class BlogService {

  public get page(): number {
    return this._page;
  }

  public get pages(): number[] {
    return Array(Math.ceil(this._count / this._blogsByPage)).fill(null).map((x, i) => i + 1);
  }

  private _count: number;
  private _page: number;
  private _blogsByPage: number;

  private _localCache: Blog[] = [];
  private _blogSubject: Subject<Blog[]>;

  constructor(private http: Http) {
    this._page = 1;
    this._count = 0;
    this._blogsByPage = 3;

    this._blogSubject = new Subject<Blog[]>();
    this.http.get('./assets/data/data.json')
      .map((res) =>  this._convertObjectToArray(res.json().blog))
      .do((x) => {
        this._localCache = x;
        this._blogSubject.next(x);
      })
      .subscribe();
  }

  getBlogs(): Observable<Blog[]> {
    return Observable.of(this._localCache)
      .merge(this._blogSubject.asObservable())
      .map((res) => {
        this._count = res.length;
        return res.reverse().slice((this._page - 1) * this._blogsByPage, (this._page) * this._blogsByPage);
      });
  }

  getBlog(key: string): Observable<Blog> {
    return Observable.of(this._localCache.filter(x => x.key === key))
      .merge( this._blogSubject.asObservable())
      .map(res => { return res.find(x => x.key === key); });
  }

  refresh() {
    this._blogSubject.next(this._localCache);
  }

  setPage(num: number) {
    this._page = num;
    this._blogSubject.next(this._localCache);
  }

  saveBlog(id: string, blog: Blog): Observable<string> {
    if (id) {
      console.warn('blog updated', blog);
      return Observable.of('blog updated. FAKE!');
    } else {
      console.warn('blog created', blog);
      return Observable.of('blog created. FAKE!');
    }
  }

  deleteBlog(id: string): Observable<string> {
    console.warn('blog deleted', id);
    return Observable.of('blog deleted FAKE');
  }

  private _convertObjectToArray(data: any): Blog[] {
    return Object.keys(data).map((key: string) => {
      return <Blog>data[key];
    });
  }
}

import { Component, OnInit, OnDestroy } from '@angular/core';
import { ActivatedRoute, Params, Router } from '@angular/router';
import { MdDialog, MdSnackBar } from '@angular/material';
import { Subscription } from 'rxjs/Subscription';
import { BlogService } from '../services/blog.service';
import { AuthService } from '../../services/auth.service';
import { Blog } from '../services/models/blog';
import { PreventDeleteDialogComponent } from '../../shared/prevent-delete-dialog.component';

@Component({
  selector: 'dr-item',
  templateUrl: './item.component.html',
  styleUrls: ['./item.component.scss']
})
export class ItemComponent implements OnInit, OnDestroy {

  public blog: Blog;
  public editMode: boolean;
  private _blogSubscription: Subscription;
  private _key: string;

  constructor(public route: ActivatedRoute, public router: Router, public blogService: BlogService, public authService: AuthService,
      public dialog: MdDialog, public snackBar: MdSnackBar) {
    this.editMode = false;
  }

  ngOnInit() {
    this.route.params.forEach((params: Params) => {
      const key = params['key'];
      if (key === 'new') {
        this.blog = null;
        this._key = null;
        this.editMode = true;
      } else {
        this._blogSubscription = this.blogService.getBlog(key).subscribe((data: any) => {
           if (data) {
            this.blog = data;
            this._key = data.$key;
          } else {
            this.router.navigate(['/']);
          }
        });
      }
    });
  }

  ngOnDestroy() {
    if (this._blogSubscription) { this._blogSubscription.unsubscribe(); }
  }

  onEditClick() {
    if (this.authService.user.isAdmin) {
      this.editMode = true;
    }
  }

  onSave(blog: Blog) {
    this.editMode = false;
    this.blogService.saveBlog(this._key, blog).subscribe(
      (m: string) => {
        if (m) {
          const snackBarRef = this.snackBar.open(m, null, { duration: 3000 });
          this.router.navigate(['/', blog.key]);
        }
      },
      (e: string) => {
        const snackBarRef = this.snackBar.open(e, null, { duration: 3000 });
      });
  }

  onDelete() {
    if (this.authService.user.isAdmin){
      const dialogRef = this.dialog.open(PreventDeleteDialogComponent);
      dialogRef.afterClosed().subscribe(result => {
        if (result) {
          this.blogService.deleteBlog(this._key).subscribe(
          (m: string) => {
            if (m) {
              const snackBarRef = this.snackBar.open(m, null, { duration: 3000 });
              this.router.navigate(['/']);
            }
          },
          (e: string) => {
            const snackBarRef = this.snackBar.open(e, null, { duration: 3000 });
          }
        );
        }
      });
    }
  }

}

