getPrevAndNext(key: string) {
  let prev: Blog = null;
  let next: Blog = null;
  const paths = key.split('_');
  if (paths.length >= 1) {
    const id: any = paths[paths.length - 1];
    if (!isNaN(id)) {
      const route = paths.slice(0, paths.length - 1).join('_');
      prev = this._localCache.find((x) => x.key === `${route}_${id - 1}`);
      next = this._localCache.find((x) => x.key === `${route}_${id + 1}`);
    }
  }
  return { prev: prev, next: next};
}
